    document.addEventListener('DOMContentLoaded', function () {
      const data = window.accountingData;
      if (!data || data.length === 0) {
        document.getElementById('accounting-stats').innerHTML = '暂无记账数据';
        return;
      }

      const yearSelect = document.getElementById('filter-year');
      const monthSelect = document.getElementById('filter-month');
      const daySelect = document.getElementById('filter-day');
      // 2. 获取新增的分类下拉框元素
      const categorySelect = document.getElementById('filter-category');
      const resetButton = document.getElementById('filter-reset');
      const statsContainer = document.getElementById('accounting-stats');
      const tbodyContainer = document.getElementById('accounting-tbody');

      const years = new Set();
      const months = new Set();
      const days = new Set();
      // 3. 新增一个Set来存储分类
      const categories = new Set();

      data.forEach(record => {
        const [year, month, day] = record.date.split('-');
        years.add(year);
        months.add(month);
        days.add(day);
        // 4. 提取所有分类
        categories.add(record.category);
      });

      [...years].sort().forEach(year => {
        yearSelect.innerHTML += `${year}`;
      });
      [...months].sort((a, b) => a - b).forEach(month => {
        monthSelect.innerHTML += `${month}月`;
      });
      [...days].sort((a, b) => a - b).forEach(day => {
        daySelect.innerHTML += `${day}日`;
      });
      // 5. 填充分类下拉框选项（按字母顺序排序）
      [...categories].sort().forEach(category => {
        categorySelect.innerHTML += `${category}`;
      });

      function filterAndRender() {
        const selectedYear = yearSelect.value;
        const selectedMonth = monthSelect.value;
        const selectedDay = daySelect.value;
        // 6. 获取选中的分类
        const selectedCategory = categorySelect.value;

        const filteredData = data.filter(record => {
          const [year, month, day] = record.date.split('-');
          const yearMatch = !selectedYear || year === selectedYear;
          const monthMatch = !selectedMonth || month === selectedMonth;
          const dayMatch = !selectedDay || day === selectedDay;
          // 7. 增加分类匹配逻辑
          const categoryMatch = !selectedCategory || record.category === selectedCategory;
          return yearMatch && monthMatch && dayMatch && categoryMatch;
        });

        let totalIncome = 0;
        let totalExpense = 0;
        filteredData.forEach(record => {
          if (record.type === 'income') {
            totalIncome += record.amount;
          } else {
            totalExpense += Math.abs(record.amount);
          }
        });
        const balance = totalIncome - totalExpense;

        statsContainer.innerHTML = `
          总收入: ${totalIncome.toFixed(2)}
          总支出: ${totalExpense.toFixed(2)}
          结余: ${balance.toFixed(2)}
        `;

        tbodyContainer.innerHTML = '';
        if (filteredData.length === 0) {
          tbodyContainer.innerHTML = '没有符合条件的记录';
        } else {
          filteredData.forEach(record => {
            tbodyContainer.innerHTML += `

                ${record.date}
                ${record.category}
                ${record.amount.toFixed(2)}
                ${record.note}

            `;
          });
        }
      }

      yearSelect.addEventListener('change', filterAndRender);
      monthSelect.addEventListener('change', filterAndRender);
      daySelect.addEventListener('change', filterAndRender);
      // 8. 为分类下拉框绑定事件
      categorySelect.addEventListener('change', filterAndRender);
      resetButton.addEventListener('click', () => {
        yearSelect.value = '';
        monthSelect.value = '';
        daySelect.value = '';
        // 9. 重置分类筛选器
        categorySelect.value = '';
        filterAndRender();
      });

      filterAndRender();
    });
